  select(
    all_of(predictor_list), starts_with("comed"), flag_ADR_TP_ID
  ) |>
  tbl_summary(
    by = flag_ADR_TP_ID,
    statistic = list(all_continuous() ~ "{median} ({p25} - {p75})"),
    missing = "no"
  ) |>
  add_overall() |>
  bold_labels()
