data_patient_complete_response_as_factor <- data_patient_complete |> mutate( flag_ADR_TP_ID = as.factor(flag_ADR_TP_ID) )
fit_univariate_regression <- function(predictor) { model <- logistic_reg() |> set_engine("glm") |> set_mode("classification") |> fit( reformulate( termlabels = predictor, response = "flag_ADR_TP_ID" ), data = data_patient_complete_response_as_factor ) }
arrange_univariate_regression <- function(model) { model |> tbl_regression( exponentiate = TRUE, show_single_row = everything() ) |> bold_p() }
plan(multisession, workers = min(parallel::detectCores() - 2, 10)) uni_model_list <- future_map(predictor_list, fit_univariate_regression) table_uni_list <- future_map(uni_model_list, arrange_univariate_regression)

