data_patient_complete_response_as_factor <- data_patient_complete |> mutate( flag_ADR_TP_ID = as.factor(flag_ADR_TP_ID) )
fit_univariate_regression <- function(predictor) { model <- logistic_reg() |> set_engine("glm") |> set_mode("classification") |> fit( reformulate( termlabels = predictor, response = "flag_ADR_TP_ID" ), data = data_patient_complete_response_as_factor ) }
arrange_univariate_regression <- function(model) { model |> tbl_regression( exponentiate = TRUE, show_single_row = everything() ) |> bold_p() }
plan(multisession, workers = min(parallel::detectCores() - 2, 10)) uni_model_list <- future_map(predictor_list, fit_univariate_regression) table_uni_list <- future_map(uni_model_list, arrange_univariate_regression)

# Helper function to add new rows to each group in a data frame. # reframe() generate new rows where its arguments are variables in the data frame. # bind_rows() bind the new rows to the original data frame. # group_modify() apply the previous functions to each group in a data frame. add_reframe_rows <- function(.data, ...) { group_modify(.data, function(x, y) { bind_rows(x, reframe(x, ...)) }) }
# This code remove 'patient_ID' that has no valid 'test_PLT' # either in 7 days prior to 'LZD_start' or during LZD use. # 1. Two rows are added to each 'patient_ID' to ensure there is at least one row # in both time periods of each patient that can be used for flagging. # 2. 'flag_post_start' is used to separate two time periods for each 'patient_ID'. # The first period is 7 days prior to 'LZD_start' and the second period is during LZD use. # 3. 'flag_remove_check' is used to check if all 'test_PLT' values are NAs # in each time periods of each 'patient_ID'. # If any time period initially had no rows, the new row added in step 1 will be used. # That group will have only one 'test_PLT' with the value NA and so the flag will be TRUE. # 4. For each 'patient_ID', if any 'flag_remove_check' is TRUE, # all rows of that 'patient_ID' will be removed. # 5. Finally, rows with 'flag_temp' as TRUE are removed. # These rows were added in step 1 and are no longer needed. inputs_labs_cleaned <- inputs_labs_pipe_1 |> group_by(patient_ID) |> arrange(test_date, .by_group = TRUE) |> add_reframe_rows(test_date = min(test_date) - 100, LZD_start = min(LZD_start), flag_temp = TRUE) |> add_reframe_rows(test_date = max(test_date) + 100, LZD_start = max(LZD_start), flag_temp = TRUE) |> mutate( flag_too_early = test_date <= LZD_start - 8, flag_post_start = test_date >= LZD_start + 1, flag_post_end = test_date >= LZD_end + 1 ) |> group_by(patient_ID, flag_post_start) |> mutate(flag_remove_check = all(is.na(test_PLT[which((!flag_too_early & !flag_post_end) | flag_temp)]))) |> group_by(patient_ID) |> filter(!any(flag_remove_check)) |> filter(is.na(flag_temp)) |> select(-flag_temp)
# inner_join() keeps only patients present in both data frames, essentially removing patients # without lab tests or other comorbidity/medication data. inputs_cleaned_joined <- inner_join(inputs_cleaned, inputs_labs_cleaned)
